
public class IterationDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
 * for
 * While
 * do While
 * 
 * for each loop(or enhanced for loop)[used for arrays and collections]
 * nested loop
 * 
 * initialization
 * condition
 * increment
 * 
 * 
 * 
 */
		for (int i=0;i<4;i++) {
			for(int j=0;j<4;j++) {
				System.out.print("*"+" ");
			}
			System.out.print("\n");
			
		}
		
	}

}
